mapper.select:
select seat - bookedseat
from carpool
where carno=#{carno}

mapper.update:
update carpool
set requestseat = requestseat+1
where carno=#{carno}

update carpool
set requestseat = requestseat-1
where carno=#{carno}

mapper.select:
select count(*) from booking
where resmem=#{mno}
and status=0
